CREATE TABLE `randonnees` (
  `id` int(11) NOT NULL,
  `nom` text DEFAULT NULL,
  `image` text NOT NULL,
  `km` float NOT NULL,
  `duree` time NOT NULL,
  `difficulte` enum('Très facile','Facile','Moyenne','Difficile','Très difficile') DEFAULT NULL,
